mapper.select:
SELECT COUNT(*) FROM (SELECT R_RECORD_ID, R_LEVEL, R_TARGET, R_DATE, REASON, RESULT, REF_ID, R_CONTENT FROM (SELECT RH.R_RECORD_ID, RH.R_LEVEL, RH.R_TARGET, RH.R_DATE, RH.REASON, RH.RESULT, RH.REF_ID, B.TEXT AS R_CONTENT FROM REPORT_HISTORY RH JOIN BOARD B ON(RH.REF_ID=B.BID) WHERE R_LEVEL = 0 AND REF_ID IN (SELECT REF_ID FROM REPORT_HISTORY WHERE R_LEVEL = 0 GROUP BY REF_ID HAVING COUNT(*) >= 5) UNION ALL SELECT RH.R_RECORD_ID, RH.R_LEVEL, RH.R_TARGET, RH.R_DATE, RH.REASON, RH.RESULT, RH.REF_ID, R.CONTENT AS R_CONTENT FROM REPORT_HISTORY RH JOIN REPLY R ON(RH.REF_ID=R.REPLY_ID) WHERE R_LEVEL = 1 AND REF_ID IN (SELECT REF_ID FROM REPORT_HISTORY WHERE R_LEVEL = 1 GROUP BY REF_ID HAVING COUNT(*) >= 5) UNION ALL SELECT RH.R_RECORD_ID, RH.R_LEVEL, RH.R_TARGET, RH.R_DATE, RH.REASON, RH.RESULT, RH.REF_ID, M.CONTENT AS R_CONTENT FROM REPORT_HISTORY RH JOIN MESSAGE M ON(RH.REF_ID=M.MSG_ID) WHERE R_LEVEL = 2) WHERE 1=1
AND R_RECORD_ID = #{rRecordId}
AND R_TARGET = #{rTarget}
AND RESULT IS NULL
)

SELECT R_RECORD_ID, R_LEVEL, R_TARGET, R_DATE, REASON, RESULT, REF_ID, R_CONTENT FROM (SELECT RH.R_RECORD_ID, RH.R_LEVEL, RH.R_TARGET, RH.R_DATE, RH.REASON, RH.RESULT, RH.REF_ID, B.TEXT AS R_CONTENT FROM REPORT_HISTORY RH JOIN BOARD B ON(RH.REF_ID=B.BID) WHERE R_LEVEL = 0 AND REF_ID IN (SELECT REF_ID FROM REPORT_HISTORY WHERE R_LEVEL = 0 GROUP BY REF_ID HAVING COUNT(*) >= 5) UNION ALL SELECT RH.R_RECORD_ID, RH.R_LEVEL, RH.R_TARGET, RH.R_DATE, RH.REASON, RH.RESULT, RH.REF_ID, R.CONTENT AS R_CONTENT FROM REPORT_HISTORY RH JOIN REPLY R ON(RH.REF_ID=R.REPLY_ID) WHERE R_LEVEL = 1 AND REF_ID IN (SELECT REF_ID FROM REPORT_HISTORY WHERE R_LEVEL = 1 GROUP BY REF_ID HAVING COUNT(*) >= 5) UNION ALL SELECT RH.R_RECORD_ID, RH.R_LEVEL, RH.R_TARGET, RH.R_DATE, RH.REASON, RH.RESULT, RH.REF_ID, M.CONTENT AS R_CONTENT FROM REPORT_HISTORY RH JOIN MESSAGE M ON(RH.REF_ID=M.MSG_ID) WHERE R_LEVEL = 2) WHERE 1=1
AND R_RECORD_ID = #{rRecordId}
AND R_TARGET = #{rTarget}
AND RESULT IS NULL
ORDER BY R_RECORD_ID DESC
